mapper.select:
SELECT count(*) FROM (
	select * from QUESTION
	where user_no = #{userNo}
)

	SELECT * FROM (
  		SELECT rownum no, t.*
  		FROM (
			SELECT * FROM question
			where user_no = #{user.userNo}
			order by question_no
		) t
		ORDER BY no ) x
	WHERE no between #{paging.startNo} AND #{paging.endNo}

SELECT count(*) FROM (
	select * from QUESTION_COMM
	where user_no = #{userNo}
)

	SELECT * FROM (
  		SELECT rownum no, t.*
  		FROM (
			SELECT * FROM question_comm
			where user_no = #{user.userNo}
			order by comm_no
		) t
		ORDER BY no ) x
	WHERE no between #{paging.startNo} AND #{paging.endNo}
